control.onPageCreate:
//滚动字幕显示控件
g5.ScrollText = function () {
    //public
    this.panel = null; //滚动字幕框
    this.dataset = new EDataSet(); //外部数据传入通道
    this.header = ""; //每句话需要加的题头信息
    this.needTime = false; //是否需要在末尾显示时间
    this.loadString = function(s) { //以字符串形式载入数据
        var ss = s.split("|");
        this.dataset.setColCount(2);
        this.dataset.setRowCount(parseInt(ss.length/2));
        for (var i = 0; i < ss.length / 2; i++) {
            this.dataset.setData(0, i, ss[i*2]);
            this.dataset.setData(1, i, ss[i*2 + 1]);
        }    
    }
    this.start = function() {
        if (this.dataset.rowCount > 0) {
            this.userIndex = (this.userIndex + 1) % this.dataset.rowCount;
            this.textIndex = (this.textIndex + 1) % this.dataset.rowCount;
            var text = "<font color = '#e03030'><strong>" + this.dataset.getData(0, this.userIndex) + "</strong></font>：" +
                this.dataset.getData(1, this.textIndex);
            this.add(text);
        }
        var t = this;
        setTimeout(function() { t.start() }, parseInt(Math.random() * 5000) + 100);
    }

    //private
    this.userIndex = 0;
    this.textIndex = 0;
    this.add = function (text) {
        var s = ee.getText(this.panel);
        if (this.needTime) {
            var now = new Date();
            text += "<font color = '#5050ff'>(" + now.getHours() + ":" + now.getMinutes() + ":" + now.getSeconds() + ")</font>";
        }
        s = this.header + text + "<br/>" + s;
        while (s.length > 2000) {                  
            var a = s.lastIndexOf("<font");
            if (a >= 0) {
                s = s.substring(0, a);
            } else break;
        }
        ee.setText(this.panel, s);
    }
}

//小数量选择控件
g5.littleIntSelector = function() {
    //public
    this.max = 100; //最大值
    this.min = 1; //最小值
    this.current = 1; //当前值或初始化值
    this.panel = null; //显示数字的panel
    this.init = function() { //初始化
        ee.setText(this.panel, this.current);
    }
    this.addClick = function () {  //点击加号按钮事件
        this.current++;
        if (this.current > this.max) this.current = this.max;
        ee.setText(this.panel, this.current);
    }
    this.decClick = function () { //点击减号按钮事件
        this.current--;
        if (this.current < this.min) this.current = this.min;
        ee.setText(this.panel, this.current);
    }
}


//省市地址选择控件
g5.postAddrSelector = function () {
    //public
    this.panel1 = null; //显示省份的控件
    this.panel2 = null; //显示市的控件
    this.panel3 = null; //显示地区的控件
    this.popWin = null; //弹出窗口最底层
    this.popWinTitle = null; //弹出窗口题头信息
    this.tableBody = null; //显示数据的tableData控件
    this.init = function() { //初始化数据
        this.fInit();
    }
    this.show = function (mode) { //弹出选择框，1,2,3分别代表省,市,地区
        var i, n1, n2, p1, p2;
        if (mode == 1) {
            this.dataset.setRowCount(this.area.length);
            for (i = 0; i < this.area.length; i++) {
                this.dataset.setData(0, i, this.area[i].name);
            }
            ee.setTableData(this.tableBody, this.dataset, 0);
            ee.setText(this.popWinTitle, "请选择省份");
            ee.showAni(this.popWin);
        } else if (mode == 2) {
            n1 = ee.getText(this.panel1);
            for (p1 = 0; p1 < this.area.length; p1++) {
                if (this.area[p1].name == n1) break;
            }
            if (p1 >= this.area.length) {
                alert("请先选择省份");
                return;
            }
            this.dataset.setRowCount(this.area[p1].length);
            for (i = 0; i < this.area[p1].length; i++) {
                this.dataset.setData(0, i, this.area[p1][i].name);
            }
            ee.setTableData(this.tableBody, this.dataset, 0);
            ee.setText(this.popWinTitle, "请选择城市");
            ee.showAni(this.popWin);
        } else if (mode == 3) {
            n1 = ee.getText(this.panel1);
            n2 = ee.getText(this.panel2);
            for (p1 = 0; p1 < this.area.length; p1++) {
                if (this.area[p1].name == n1) break;
            }
            if (p1 >= this.area.length) {
                alert("请先选择省份");
                return;
            }
            for (p2 = 0; p2 < this.area[p1].length; p2++) {
                if (this.area[p1][p2].name == n2) break;
            }
            if (p2 >= this.area[p1].length) {
                alert("请先选择城市");
                return;
            }
            this.dataset.setRowCount(this.area[p1][p2].length);
            for (i = 0; i < this.area[p1][p2].length; i++) {
                this.dataset.setData(0, i, this.area[p1][p2][i]);
            }
            ee.setTableData(this.tableBody, this.dataset, 0);
            ee.setText(this.popWinTitle, "请选择地区");
            ee.showAni(this.popWin);
        }
        this.mode = mode;
    }
    this.onClick = function(sender) { //点击选择地区回叫事件
        var lastName;
        var name = ee.getText(sender);
        if (this.mode == 1) {
            lastName = ee.getText(this.panel1);
            if (lastName != name) {
                ee.setText(this.panel2, "选择城市");
                ee.setText(this.panel3, "选择地区");
            }
            ee.setText(this.panel1, name);
        } else if (this.mode == 2) {
            lastName = ee.getText(this.panel2);
            if (lastName != name) {
                ee.setText(this.panel3, "选择地区");
            }
            ee.setText(this.panel2, name);
        } else if (this.mode == 3) {
            ee.setText(this.panel3, ee.getText(sender));
        }
        ee.setVisible(this.popWin, false);
    }
    this.getAddress = function() { //获取用户选择的地址
        var n1 = ee.getText(this.panel1);
        var n2 = ee.getText(this.panel2);
        var n3 = ee.getText(this.panel3);
        if (n3 == "选择地区") return "";
        return n1 + " " + n2 + " " + n3;
    }

    //private
    this.mode = 1;
    this.dataset = new EDataSet();
    this.dataset.setColCount(1);
    this.area = new Array(); //城市地区主数据库
    this.add = function (level, buff) {
        var l = level.split("_");
        if (level == "") {
            l.length = 0;
        }
        var b = buff.split("_");
        var i, item;
        for (i = 0; i < l.length; i++) l[i] = parseInt(l[i]);
        if (l.length == 2) {
            if (eb.isEmpty(this.area[l[0]])) this.area[l[0]] = new Array();
            if (eb.isEmpty(this.area[l[0]][l[1]])) this.area[l[0]][l[1]] = new Array();
            for (i = 0; i < b.length; i++) {
                this.area[l[0]][l[1]].push(b[i]);
            }
        } else if (l.length == 1) {
            if (eb.isEmpty(this.area[l[0]])) this.area[l[0]] = new Array();
            for (i = 0; i < b.length; i++) {
                if ((b[i].charAt(0)<='Z')&amp;&amp;(b[i].charAt(0)>='A')) {
                    b[i] = b[i].substring(1, b[i].length);
                }
                if (eb.isEmpty(this.area[l[0]][i])) {
                    this.area[l[0]][i] = new Array();
                }
                this.area[l[0]][i].name = b[i];
            }
        } else if (l.length == 0) {
            for (i = 0; i < b.length; i++) {
                if (eb.isEmpty(this.area[i])) {
                    this.area[i] = new Array();
                }
                this.area[i].name = b[i];
            }
        }
    }
    this.fInit = function () {
        this.add("0_0", "东城区_西城区_崇文区_宣武区_朝阳区_丰台区_石景山区_海淀区_门头沟区_房山区_通州区_顺义区_昌平区_大兴区_怀柔区_平谷区_密云县_延庆县_延庆镇");
        this.add("0", "B北京市");
        this.add("1_0", "和平区_河东区_河西区_南开区_河北区_红桥区_塘沽区_汉沽区_大港区_东丽区_西青区_津南区_北辰区_武清区_宝坻区_蓟县_宁河县_芦台镇_静海县_静海镇");
        this.add("1", "T天津市");
        this.add("2_0", "黄浦区_卢湾区_徐汇区_长宁区_静安区_普陀区_闸北区_虹口区_杨浦区_闵行区_宝山区_嘉定区_浦东新区_金山区_松江区_青浦区_南汇区_奉贤区_崇明县_城桥镇");
        this.add("2", "S上海市");
        this.add("3_0", "渝中区_大渡口区_江北区_沙坪坝区_九龙坡区_南岸区_北碚区_万盛区_双桥区_渝北区_巴南区_万州区_涪陵区_黔江区_长寿区_合川市_永川区市_江津市_南川市_綦江县_潼南县_铜梁县_大足县_荣昌县_璧山县_垫江县_武隆县_丰都县_城口县_梁平县_开县_巫溪县_巫山县_奉节县_云阳县_忠县_石柱土家族自治县_彭水苗族土家族自治县_酉阳土家族苗族自治县_秀山土家族苗族自治县");
        this.add("3", "C重庆市");
