taskSetItem.script1:
var userId = 13;
var productId = g5.productId;
var linkId = g5.linkId;
var param1 = "";
var param2 = "";
var address = g5.addrSelector.getAddress();
if (address == "") {
    alert("请选择有效的地址");
    return;
}
if (ee.getEditText(ee.getControl("edit3")).length < 3) {
    alert("请填写有效的邮寄地址");
    return;
}
address += " " + ee.getEditText(ee.getControl("edit3"));
var mobile = ee.getEditText(ee.getControl("edit2"));
var name = ee.getEditText(ee.getControl("edit1"));
var count = g5.countSelector.current;
var note = ee.getEditText(ee.getControl("edit4"));

eio.appendInt32(userId);
eio.appendInt32(productId);
eio.appendInt32(linkId);
eio.appendString16(param1);
eio.appendString16(param2);
eio.appendString16(address);
eio.appendString16(mobile);
eio.appendString16(name);
eio.appendInt32(count);
eio.appendString16(note);

taskSetItem.script2:
var userId = eio.readInt32();
var productId = eio.readInt32();
var linkId = eio.readInt32();
var param1 = eio.readString16();
var param2 = eio.readString16();
var address = eio.readString16();
var mobile = eio.readString16();
var name = eio.readString16();
var count = eio.readInt32();
var note = eio.readString16();

//后台认证
if (address.length < 10) {
    eio.appendString8("F");
    eio.appendString16("请填写有效的邮寄地址");
    es.logWarning("请填写有效的邮寄地址");
    return;
}
if (mobile.length < 8) {
    eio.appendString8("F");
    eio.appendString16("请填写有效的联系手机号码");
    es.logWarning("请填写有效的联系手机号码");
    return;
}
if (name.length < 2) {
    eio.appendString8("F");
    eio.appendString16("请填写有效的联系人姓名，如果您不方便透露姓名可以填写为类似“张先生”的形式");
    es.logWarning("请填写有效的联系人姓名，如果您不方便透露姓名可以填写为类似“张先生”的形式");
    return;
}

var sql,sqlResult;
sql = "exec orderCreate " +
    userId + "," +
    productId + "," +
    linkId + "," +
    "'" + db.encode(param1) + "'," +
    "'" + db.encode(param2) + "'," +
    "'" + db.encode(address) + "'," +
    "'" + db.encode(mobile) + "'," +
    "'" + db.encode(name) + "'," +
    count + "," +
    "'" + db.encode(note) + "'";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getInt32(sqlResult, "errorCode") < 0) {
    eio.appendString8("F");
    eio.appendString8(db.getString(sqlResult, "errorString"));
    return;
}

eio.appendString8("T");
eio.appendString8(db.getString(sqlResult, "errorString"));

taskSetItem.script3:
if (eio.readString8() != "T") {
    alert("下单失败：" + eio.readString16());
    return;
}

alert("下单成功，我们将尽快核实您的信息并发货，请保持手机畅通");

taskSetItem.script2:
var linkId = eio.readInt32();

var sql,sqlResult;

//页面一般参数
sql = "select * from [link] where id = " + linkId;
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    eio.appendString8("系统繁忙，请稍后再试");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
if (!db.read(sqlResult)) {
    eio.appendString8("F");
    eio.appendString8("linkId非法");
    es.logWarning("linkId非法, sql=" + sql);
    return;
}
eio.appendString8("T");
eio.appendString16(db.getString(sqlResult, "pageTitle"));
eio.appendString16(db.getString(sqlResult, "pageCompany"));
eio.appendString16(db.getString(sqlResult, "pageTelA"));
eio.appendString16(db.getString(sqlResult, "pageTelB"));

//用户评论
sql = "select * from linkChat where linkId = " + linkId +
    " and [status] = 'CHT' order by sortValue ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.clear();
    eio.appendString8("F");
    eio.appendString8("系统繁忙，请稍后再试");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendDataSet(sqlResult,"userName,userChat", 0, 1000); 

//下单记录
sql = "select * from linkChat where linkId = " + linkId +
    " and [status] = 'BUY' order by sortValue ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.clear();
    eio.appendString8("F");
    eio.appendString8("系统繁忙，请稍后再试");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendDataSet(sqlResult,"userName,userChat", 0, 1000);

taskSetItem.script3:
if (eio.readString8() != "T") {
    alert("请刷新页面");
    return;
}

g5.pageTitle = eio.readString16();
g5.pageCompany = eio.readString16();
g5.pageTelA = eio.readString16();
g5.pageTelB = eio.readString16();

g5.datasetChat = eio.readDataSet();
g5.datasetBuy = eio.readDataSet(); 

//刷新页面
ee.setText(ee.getControl("panel1"), g5.pageTitle);
ee.setText(ee.getControl("panel88"), "咨询热线：" + g5.pageTelA);
ee.setText(ee.getControl("panel238"), g5.pageCompany);
g5.userComment.dataset = g5.datasetChat;
g5.orderList.dataset = g5.datasetBuy;
